mapper.select:
        SELECT


--         级联表的字段
        ,exampaper.exampaper_name as exampaperName
        ,exampaper.exampaper_date as exampaperDate
        ,exampaper.exampaper_myscore as exampaperMyscore
        ,exampaper.exampaper_types as exampaperTypes
        ,exampaper.exampaper_delete as exampaperDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.yonghu_delete as yonghuDelete

        FROM examrecord  a
        left JOIN exampaper exampaper ON a.exampaper_id = exampaper.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and a.examrecord_uuid_number like CONCAT('%',#{params.examrecordUuidNumber},'%')


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and (
                    a.exampaper_id = #{params.exampaperId}
                )


                  and a.total_score >= #{params.totalScoreStart}   


                  and a.total_score <= #{params.totalScoreEnd}   


                and a.total_score = #{params.totalScore}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.exampaper_id IS NOT NULL


                and exampaper.exampaper_name like CONCAT('%',#{params.exampaperName},'%')


                  and exampaper.exampaper_date >= #{params.exampaperDateStart}   


                  and exampaper.exampaper_date <= #{params.exampaperDateEnd}   


                and exampaper.exampaper_date = #{params.exampaperDate}


                  and exampaper.exampaper_myscore >= #{params.exampaperMyscoreStart}   


                  and exampaper.exampaper_myscore <= #{params.exampaperMyscoreEnd}   


                and exampaper.exampaper_myscore = #{params.exampaperMyscore}


                and exampaper.exampaper_types = #{params.exampaperTypes}



                  and exampaper.exampaper_delete >= #{params.exampaperDeleteStart}   


                  and exampaper.exampaper_delete <= #{params.exampaperDeleteEnd}   


                and exampaper.exampaper_delete = #{params.exampaperDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


                and yonghu.yonghu_delete = #{params.yonghuDelete}



        order by a.${params.orderBy} desc
