mapper.select:
        SELECT


--         级联表的字段

        FROM exampaper  a



                and a.id in

                #{item}



                and a.exampaper_name like CONCAT('%',#{params.exampaperName},'%')


                  and a.exampaper_date >= #{params.exampaperDateStart}   


                  and a.exampaper_date <= #{params.exampaperDateEnd}   


                and a.exampaper_date = #{params.exampaperDate}


                  and a.exampaper_myscore >= #{params.exampaperMyscoreStart}   


                  and a.exampaper_myscore <= #{params.exampaperMyscoreEnd}   


                and a.exampaper_myscore = #{params.exampaperMyscore}


                and a.exampaper_types = #{params.exampaperTypes}


                  and a.exampaper_delete >= #{params.exampaperDeleteStart}   


                  and a.exampaper_delete <= #{params.exampaperDeleteEnd}   


                and a.exampaper_delete = #{params.exampaperDelete}




        order by a.${params.orderBy} desc
